<script>
    document.addEventListener("DOMContentLoaded", function () {
        // Size toggle
        const sizeRadios = document.querySelectorAll("input[name='product_size']");
        const selectedSize = document.getElementById("selectedSize");
        let lastSelectedSize = null;

        sizeRadios.forEach(radio => {
            radio.addEventListener("click", function () {
                if (lastSelectedSize === this) {
                    this.checked = false;
                    selectedSize.textContent = "Select Any Size";
                    lastSelectedSize = null;
                } else {
                    lastSelectedSize = this;
                    selectedSize.textContent = this.value || "None";
                }
                toggleActionButtons();
            });
        });

        // Color toggle
        const colorRadios = document.querySelectorAll("input[name='product_color']");
        const selectedColor = document.getElementById("selectedColor");
        let lastSelectedColor = null;

        colorRadios.forEach(radio => {
            radio.addEventListener("click", function () {
                if (lastSelectedColor === this) {
                    this.checked = false;
                    selectedColor.textContent = "Select Any Color";
                    lastSelectedColor = null;
                } else {
                    lastSelectedColor = this;
                    selectedColor.textContent = this.value || "None";
                }
                toggleActionButtons();
            });
        });

        // Toggle buttons and price
        function toggleActionButtons() {
            const addBtn = document.querySelector(".add_cart_btn");
            const buyBtn = document.querySelector(".order_now_btn_mm");
            const priceSpan = addBtn; // Assuming price is inside Add to Cart button

            if (lastSelectedSize && lastSelectedColor) {
                // Enable buttons
                if (addBtn) {
                    addBtn.classList.remove("blocked");
                    addBtn.style.opacity = 1;
                    addBtn.style.pointerEvents = "auto";
                    // Show price
                    addBtn.textContent = "Add to cart – <?php echo e($details->new_price); ?>৳";
                }
                if (buyBtn) {
                    buyBtn.classList.remove("blocked");
                    buyBtn.style.opacity = 1;
                    buyBtn.style.pointerEvents = "auto";
                }
            } else {
                // Disable buttons
                if (addBtn) {
                    addBtn.classList.add("blocked");
                    addBtn.style.opacity = 0.4;
                    addBtn.style.pointerEvents = "none";
                    // Hide price (or reset)
                    addBtn.textContent = "Add to cart";
                }
                if (buyBtn) {
                    buyBtn.classList.add("blocked");
                    buyBtn.style.opacity = 0.4;
                    buyBtn.style.pointerEvents = "none";
                }
            }
        }

        // Initialize on page load
        toggleActionButtons();
    });
</script>
<script>
document.addEventListener("DOMContentLoaded", function () {

    const minusBtn = document.querySelector(".pro-qty .minus");
    const plusBtn = document.querySelector(".pro-qty .plus");
    const qtyInput = document.querySelector(".pro-qty input[name='qty']");
    const addCartBtn = document.querySelector(".add_cart_btn");
    const buyItBtn = document.querySelector(".order_now_btn_mm");

    let basePrice = parseFloat("<?php echo e($basePrice); ?>");
    const hasSize = <?php echo e($productsizes->count() > 0 ? 'true' : 'false'); ?>;
    const hasColor = <?php echo e($productcolors->count() > 0 ? 'true' : 'false'); ?>;
    // Size & Color check
    const sizeRadios = document.querySelectorAll("input[name='product_size']");
    let lastSelectedSize = null;
    sizeRadios.forEach(radio => {
        radio.addEventListener("click", function () {
            lastSelectedSize = this.checked ? this : null;
            updateAddCartPrice();
        });
    });

    const colorRadios = document.querySelectorAll("input[name='product_color']");
    let lastSelectedColor = null;
    colorRadios.forEach(radio => {
        radio.addEventListener("click", function () {
            lastSelectedColor = this.checked ? this : null;
            updateAddCartPrice();
        });
    });

    // Update Add Cart price based on current quantity
    function updateAddCartPrice() {
        let quantity = parseInt(qtyInput.value) || 1;

        // যদি product e size বা color না থাকে → সবসময় visible
        if (!hasSize && !hasColor) {
            addCartBtn.textContent = "Add to cart – " + (basePrice * quantity).toFixed(2) + "৳";
            addCartBtn.style.opacity = 1;
            addCartBtn.style.pointerEvents = "auto";
            if (buyItBtn) {
                buyItBtn.style.opacity = 1;
                buyItBtn.style.pointerEvents = "auto";
            }
            return;
        }

        // যদি দুইটা option (size + color) থাকে → দুটোই select করতে হবে
        if ((hasSize ? lastSelectedSize : true) && (hasColor ? lastSelectedColor : true)) {
            addCartBtn.textContent = "Add to cart – " + (basePrice * quantity).toFixed(2) + "৳";
            addCartBtn.style.opacity = 1;
            addCartBtn.style.pointerEvents = "auto";
            if (buyItBtn) {
                buyItBtn.style.opacity = 1;
                buyItBtn.style.pointerEvents = "auto";
            }
        } else {
            addCartBtn.textContent = "Add to cart";
            addCartBtn.style.opacity = 0.4;
            addCartBtn.style.pointerEvents = "none";
            if (buyItBtn) {
                buyItBtn.style.opacity = 0.4;
                buyItBtn.style.pointerEvents = "none";
            }
        }
    }

    // Minus button
    minusBtn.addEventListener("click", function (e) {
        e.preventDefault();
        let quantity = parseInt(qtyInput.value) || 1;

        // ✅ যদি size/color না থাকে → সবসময় কাজ করবে
        if (!hasSize && !hasColor) {
            if (quantity > 1) quantity -= 1;
            addCartBtn.textContent = "Add to cart – " + (basePrice * quantity).toFixed(2) + "৳";
            return;
        }

        // ✅ যদি যেটা আছে সেটা select করা থাকে
        const sizeOk = hasSize ? lastSelectedSize : true;
        const colorOk = hasColor ? lastSelectedColor : true;

        if (sizeOk && colorOk && quantity > 1) {
            quantity -= 1;
            addCartBtn.textContent = "Add to cart – " + (basePrice * quantity).toFixed(2) + "৳";
        }
    });

    // Plus button
    plusBtn.addEventListener("click", function (e) {
        e.preventDefault();
        let quantity = parseInt(qtyInput.value) || 1;

        // ✅ যদি size/color না থাকে → সবসময় কাজ করবে
        if (!hasSize && !hasColor) {
            quantity += 1;
            addCartBtn.textContent = "Add to cart – " + (basePrice * quantity).toFixed(2) + "৳";
            return;
        }

        // ✅ যদি যেটা আছে সেটা select করা থাকে
        const sizeOk = hasSize ? lastSelectedSize : true;
        const colorOk = hasColor ? lastSelectedColor : true;

        if (sizeOk && colorOk) {
            quantity += 1;
            addCartBtn.textContent = "Add to cart – " + (basePrice * quantity).toFixed(2) + "৳";
        }
    });

    // Initialize display
    updateAddCartPrice();
});
</script>
